<?phpdefined('BASEPATH') OR exit('<h1 style="text-transform: uppercase; font-weight: 300; color: red; text-align:center; margin-top: 20%;">Acesso não permitido</h1>');class Dashboard extends CI_Controller {        public function __construct() {        parent::__construct();	if ((!$this->session->userdata('session_id')) || (!$this->session->userdata('logado'))) {	    $this->session->set_flashdata('error','Você precisa esta logado para continuar.');	    redirect(base_url('login'));	}               	$this->load->model('dashboard_model','',TRUE);        $this->load->model('usuarios_model','',TRUE);        $this->load->helper('data_helper');        $this->load->model('all_model','',TRUE);	$this->data['home'] = 'Home';        $this->data['title'] = 'Home';	$this->data['dadosUsuario'] = $this->all_model->obterID('user', 'user_perfil', 'user_perfil.id_user = user.idUser', 'idUser', $this->session->userdata('idUser'));        $this->data['config'] = $this->all_model->obterID('config_sistema','','','idConfig', '1');    }    public function index() {        $this->data['view'] = 'dashboard/home';        $this->load->view('tema/home',  $this->data);    }        public function configs() {        $data_config = array(            //liberar cadastro de usuario            'cUsuario' => $this->input->post('cUsuario')        );        $data_config = serialize($data_config);        $data = array(            'configs_sistema' => $data_config        );        if ($this->all_model->edit('config_sistema', $data, 'idConfig', '1') == TRUE) {            echo json_encode(array('result' => true, 'mensagen' => 'Configurações atualizada.'));        }else{            echo json_encode(array('result' => false, 'mensagen' => 'Ops, houve um error!'));        }    }}