<?phpdefined('BASEPATH') OR exit('<h1 style="text-transform: uppercase; font-weight: 300; color: red; text-align:center; margin-top: 20%;">Acesso não permitido</h1>');class Site extends CI_Controller {        public function __construct() {        parent::__construct();	if ((!$this->session->userdata('session_id')) || (!$this->session->userdata('logado'))) {	    $this->session->set_flashdata('error','Você precisa esta logado para continuar.');	    redirect('login');	}       	$this->load->model('dashboard_model','',TRUE);        $this->load->model('usuarios_model','',TRUE);        $this->load->helper('data_helper');        $this->load->model('all_model','',TRUE);	$this->data['getUser'] = $this->all_model->getById('user', 'user_perfil', 'user_perfil.id_user = user.idUser', 'idUser', $this->session->userdata('idUser'));        $this->data['config'] = $this->all_model->getById('config_sistema','','','idConfig', '1');//        $verifica = $this->data['getUser'];//        if($verifica->cpf == NULL || $verifica->rg == NULL || $verifica->genero == NULL || $verifica->dataNasc == '0000-00-00' || $verifica->cep == NULL || $verifica->numero == NULL){//            $this->session->set_flashdata('info','Você precisa completa seu dados para continuar usando o sistema');//            redirect(base_url('perfil'));//        }    }        public function index() {	$this->data['menuHome'] = 'Home';	$this->data['view'] = 'site/site';        $this->load->view('template/header', $this->data);        $this->load->view('template/menu',  $this->data);	$this->load->view('template/home');	$this->load->view('template/rodape');    }}