<?phpdefined('BASEPATH') OR exit('<h1 style="text-transform: uppercase; font-weight: 300; color: red; text-align:center; margin-top: 20%;">Acesso não permitido</h1>');function modal_excluir($base_url_action=null, $idForm , $mensagem=null){        $modal_excluir = "        <div class='modal modal-center fade' id='excluir' tabindex='-1' role='dialog' aria-labelledby='myModalLabel'>            <div class='modal-dialog' role='document'>                <form action='".base_url($base_url_action)."' id='$idForm' method='post' >                    <div class='modal-content'>                        <div class='modal-header'>                            <h4 class='modal-title' id='myModalLabel'>Notificação de exclusão</h4>                        </div>                        <div class='modal-body'>                            <input type='hidden' id='acaoID' name='id' value='' />                            <div class='mensagem lead'><p style='text-align: center'>Deseja realmente excluir este  $mensagem?</p></div>                        </div>                        <div class='modal-footer'>                            <button type='button' class='btn btn-danger' data-dismiss='modal'>Não</button>                            <button class='btn btn-success'>Sim</button>                        </div>                    </div>                </form>            </div>        </div>    ";      return $modal_excluir;}