<?phpfunction removeColocarEspaco($string) {    if (is_string($string)):        $string = strtolower(trim(utf8_decode($string)));        $before = 'ÀÁÃÄÅÆÇÈÉÊËÌÍÎÏÐÑÒÓÔÕÖØÙÚÛÜÝÞßàáãäåæçèéêëìíîïðñòóôõöøùúûýýþÿ';        $after = 'aaaaaaaceeeeiiiidnoooooouuuuybsaaaaaaaceeeeiiiidnoooooouuuyy';        $string = strtr($string, utf8_decode($before), $after);        $replace = array(            '/[^a-z0-9.-]/' => '',            '/\-{2,}/' => ''        );        $string = preg_replace(array_keys($replace), array_values($replace), $string);    endif;    return $string;}function limpaCPF_CNPJ($valor){    $valor = trim($valor);    $valor = str_replace(".", "", $valor);    $valor = str_replace(",", "", $valor);    $valor = str_replace("-", "", $valor);    $valor = str_replace("/", "", $valor);    return $valor;}