<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Lockscreen_model extends CI_Model {    function __construct() {        parent::__construct();    }   function get($table,$fields,$where='',$perpage=0,$start=0,$one=false,$array='array'){        $this->db->select($fields);        $this->db->from($table);        $this->db->limit($perpage,$start);        $query = $this->db->get();        $result =  !$one  ? $query->result() : $query->row();        return $result;    }    function count($table) {	return $this->db->count_all($table);    }    function getLockscreen($getUser) {        $this->db->select('*');        $this->db->from('user');        $this->db->join();        $this->db->where('email_user', $getUser);        $this->db->limit(1);        return $this->db->get()->row();    }}