<div class="main-content">    <div class="row">        <div class="col-12">            <div class="card">                <?php if($this->permission->checkPermission($this->session->userdata('permissao'),'aCliente')){ ?>                    <h4 class="card-title">                        <button onclick="location.href = '<?php echo base_url('clientes/adicionar'); ?>'" class="btn btn-primary text-right"><i class="icon ti-plus"></i> Adicionar Cliente</button>                    </h4>                  <?php } ?>                <div class="card-body">                    <p>Lista de clientes cadastrada no sistema.</p>                    <br>                    <?php                        if ($this->session->flashdata('error') != null || $this->session->flashdata('success') != null) {                            if($this->session->flashdata('success') == TRUE){                                $alert = 'primary';                            } else {                                $alert = 'danger';                            }                    ?>                    <div class="alert alert-<?php echo $alert; ?>" role="alert">                            <?php                                if ($this->session->flashdata('success') == TRUE) {                                    echo '<i class="zmdi zmdi-check"></i> '. $this->session->flashdata('success');                                 } else {                                    echo 'Ops, um error encontrado! <br> <b>Drescrição do Erro:</b> <br>'. $this->session->flashdata('error');                                 }                            ?>                    </div>                    <?php } ?>                    <table class="table table-striped" cellspacing="0" data-provide="datatables">                        <thead>                            <tr>                                <th>#</th>                                <th>Razão Social</th>                                <th>Nome Fantasia</th>                                <th>Telefone</th>                                <th>Documento</th>                                <th>Ações</th>                            </tr>                        </thead>                        <tbody>                                <?php if(!$obterCliente){?>                                    <tr class="text-center"><td colspan="6">Nenhuma cliente encontrado.</td></tr>                                <?php                                 }else{                                     foreach ($obterCliente as $r) {                                ?>                                <tr>                                    <td><?php echo $r->id_clientes; ?></td>                                    <td>                                        <?php echo $r->razao_social_cliente; ?> <br>                                        <small>Cadastro em <i><?php echo dataFormatoBRcHora($r->data_cadastro); ?></i></small>                                    </td>                                    <td><?php if($r->nome_fantasia_cliente == null){echo 'Não incluido';}else{ echo $r->nome_fantasia_cliente;} ?></td>                                    <td><?php if($r->telefone_cliente == null){echo 'Não incluido';}else{ echo $r->telefone_cliente;} ?></td>                                    <td><?php echo $r->cnpj_cpf; ?></td>                                    <td class="text-right table-actions">                                        <a class="table-action hover-primary" href="<?php echo base_url("clientes/editar/$r->id_clientes"); ?>"><i class="ti-pencil"></i></a>                                        <a class="table-action hover-danger" clientes="<?php echo $r->id_clientes; ?>" href="#excluir"  role="button" data-toggle="modal" id="excluirCliente"><i class="ti-trash"></i></a>                                    </td>                                 </tr>                                     <?php } ?>                                <?php } ?>                            </tr>                        </tbody>                    </table>                </div>            </div>        </div>    </div></div><?php echo modal_excluir('clientes/excluir', 'Cliente') ?><script type="text/javascript"> $('#dadosCliente').on('show.bs.modal', function (event) {    var button = $(event.relatedTarget) // Button that triggered the modal    // Extract info from data-* attributes    var recipient = button.data('whatever')    var recipient1 = button.data('whatever1')    var recipient2 = button.data('whatever2')    var recipient3 = button.data('whatever3')    var recipient4 = button.data('whatever4')    var recipient5 = button.data('whatever5')    var recipient6 = button.data('whatever6')    var recipient7 = button.data('whatever7')    var recipient8 = button.data('whatever8')    var recipient9 = button.data('whatever9')    var recipient10 = button.data('whatever10')    var modal = $(this)    modal.find('.codigo').text(recipient);    modal.find('.razao').text(recipient1);    modal.find('.fantasia').text(recipient2);    modal.find('.pessoa').text(recipient3);    modal.find('.tipoie').text(recipient4);    modal.find('.documento').text(recipient5);    modal.find('.telefone').text(recipient6);    modal.find('.ie-cnae-im').text(recipient7);    modal.find('.endereco').text(recipient8);    modal.find('.data-cadastro').text(recipient9);    modal.find('.data-modificado').text(recipient10);    modal.find('.editar').html('<a class="btn btn-info" href="<?php echo base_url('clientes/editar/');?>/'+recipient+'?p='+recipient3+'"> Editar </a>');    modal.find('.excluir').html('<a class="btn btn-danger" href="<?php echo base_url('clientes/excluirModal');?>/'+recipient+'"> Excluir </a>');})</script>