                    <input type="password" class="form-control" name="senha" id="senha">                            <label for="password">Senha</label>                        </div>                        <div class="form-group flexbox">                            <label class="custom-control custom-checkbox">                                <input type="checkbox" class="custom-control-input" checked>                                <span class="custom-control-indicator"></span>                                <span class="custom-control-description">Lembra me</span>                            </label>                            <a class="text-muted hover-primary fs-13" href="#" data-toggle="modal" data-target="#modal-fill">Esqueceu senha?</a>                        </div>                        <br>                        <button class="btn btn-bold btn-block btn-primary" type="submit"><i class="fa fa-unlock-alt mr-2"></i> Desbloquear</button>                    </form>                    <p class="text-center text-muted fs-13 mt-20">Não tem uma conta? <a class="text-primary fw-500" href="#">Cadastrar</a></p>                                    </div>        <div class="modal modal-fill fade" data-backdrop="false" id="modal-fill" tabindex="-1">                <div class="modal-dialog">                    <div class="modal-content">                        <div class="modal-header">                            <h5 class="modal-title">Redifinir Senha</h5>                            <button type="button" class="close" data-dismiss="modal">                                <span aria-hidden="true">&times;</span>                            </button>                            <button style="margin-top: 15px;" type="button" class="close" data-dismiss="modal">                                <span style="font-size: .85rem;">esc</span>                            </button>                        </div>                        <div class="modal-body form-type-material">                            <form class="form-type-material mt-0" id="autenticaSenha">                                <div class="form-group">                                    <input type="text" class="form-control" id="email" name="email">                                    <label for="username">E-mail</label>                                </div>                                <div class="form-group">                                    <button class="btn btn-bold btn-block btn-primary" id="btn_login">Recuperar Senha</button>                                </div>                            </form>                        </div>                        <div class="modal-footer">                            <button type="button" class="btn btn-bold btn-pure btn-primary">Logar</button>                        </div>                    </div>                </div>            </div>        <!-- Scripts -->        <script src="<?php echo base_url('assets/js/core.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/app.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/script.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/valida.js') ?>"></script>        <script type="text/javascript">            $(document).ready(function () {                $('#email').focus();                $("#autenticaLogin").validate({                    rules: {                        email: {required: true, email: true},                        senha: {required: true, minlength: 6}                    },                    messages: {                        email: {required: 'Campo Requerido.', email: 'Insira Email válido'},                        senha: {required: 'Campo Requerido.', minlength: jQuery.validator.format("Pelo menos {0} caracteres são necessários!")}                    },                    submitHandler: function (form) {                        var dados = $(form).serialize();                        $.ajax({                            type: "POST",                            url: "<?php echo base_url('login/autenticarUser?ajax=true'); ?>",                            data: dados,                            dataType: 'json',                            success: function (data) {                                if (data.result == true) {                                    window.location.href = "<?php echo base_url('dashboard'); ?>";                                } else {                                    app.modaler({                                        html: 'Os dados de acesso estão incorretos, por favor tente novamente!',                                        type: 'bottom',                                        title: 'Error ao Acessar',                                        size: 'sm',                                    });                                }                            }                        });                        return false;                    },                    errorClass: "is-invalid",                    errorElement: "invalid-feedback",                    highlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').addClass('is-invalid');                    },                    unhighlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').removeClass('is-invalid');                        $(element).parents('.is-valid').addClass('is-valid');                    }                });                                $("#autenticaSenha").validate({                    rules: {                        email: {required: true, email: true}                    },                    messages: {                        email: {required: 'O campo email é obrigatorio.', email: 'O campo email deve conter um email válido.'}                    },                     submitHandler: function (form) {                        var dados = $(form).serialize();                        $.ajax({                            type: "POST",                            url: "<?php echo base_url('login/recuperar'); ?>",                            data: dados,                            dataType: 'json',                            beforeSend: function() {	                                $("#btn_login").html("Aguarde..... Validando dados!");                            },                            success: function (data){                                if (data.result == true) {                                    $("#autenticaSenha")[0].reset();                                    app.toast(data.mensagen);                                    $("#btn_login").html('Recuperar Senha');                                } else {                                    $("#btn_login").html('Recuperar Senha');                                    $(".msg").css('display', 'block')                                    app.toast(data.mensagen);                                }                            }                        });                        return false;                    },                    errorClass: "help-inline",                    errorElement: "span",                    highlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').addClass('is-invalid');                    },                    unhighlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').removeClass('is-invalid');                        $(element).parents('.is-valid').addClass('is-valid');                    }                });                                $(document).on('click', '.close', function (event) {                    $("#autenticaSenha")[0].reset();                });            });        </script>    </body></html>