<!DOCTYPE html><?php $config = unserialize($config->configs_sistema); ?><html lang="en">    <head>        <meta charset="utf-8">        <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">        <meta name="description" content="Responsive admin dashboard and web application ui kit. ">        <meta name="keywords" content="login, signin">        <title>Login &mdash; Sky Painel</title>        <!-- Fonts -->        <link href="https://fonts.googleapis.com/css?family=Roboto:100,300,400,500,300i" rel="stylesheet">        <!-- Styles -->        <link href="<?php echo base_url('assets/css/core.min.css'); ?>" rel="stylesheet">        <link href="<?php echo base_url('assets/css/app.min.css'); ?>" rel="stylesheet">        <link href="<?php echo base_url('assets/css/style.min.css'); ?>" rel="stylesheet">        <!-- Favicons -->        <link rel="apple-touch-icon" href="<?php echo base_url('assets/img/apple-touch-icon.png'); ?>">        <link rel="icon" href="<?php echo base_url('assets/img/favicon.png'); ?>">    </head>    <body>        <div class="row no-gutters min-h-fullscreen bg-white ">            <div class="col-md-6 col-lg-7 col-xl-8 d-none d-md-block bg-img" style="background-image: url(<?php echo base_url('assets/img/lobo1.jpg'); ?>)" data-overlay="5">                <div class="row h-100 pl-50  animated slideInRight" id="animationSandbox">                    <div class="col-md-10 col-lg-8 align-self-end">                        <img src="<?php echo base_url('assets/img/logo-light-lg.png'); ?>" alt="...">                        <br><br><br>                        <h4 class="text-white">Sky Painel Controle é o melhor sistema adminsitrativo online disponível.</h4>                        <p class="text-white">controle de forma confiável a sua empresa por completo via internet. Estratégias de construção de equipes com firmeza.</p>                        <br><br>                    </div>                </div>            </div>            <div class="col-md-6 col-lg-5 col-xl-4 align-self-center">                <div class="px-80 py-30">                    <h4>Login</h4>                    <p><small>Faça login em sua conta</small></p>                    <br>                    <?php if ($this->session->flashdata('error') != null) { ?>                          <div class="alert alert-danger text-center" role="alert">                            <?php echo $this->session->flashdata('error'); ?>                        </div>                    <?php } elseif ($this->session->flashdata('success') != null) { ?>                          <div class="alert alert-success text-center" role="alert">                            <?php echo $this->session->flashdata('success'); ?>                        </div>                    <?php } ?>                    <form class="form-type-material" id="autenticaLogin">                        <div class="form-group">                            <input type="text" class="form-control" id="email" name="email">                            <label for="username">E-mail</label>                        </div>                        <div class="form-group">                            <input type="password" class="form-control" name="senha">                            <label for="inputDanger1">Senha</label>                        </div>                        <div class="form-group flexbox">                            <label class="custom-control custom-checkbox">                                <input type="checkbox" class="custom-control-input" checked>                                <span class="custom-control-indicator"></span>                                <span class="custom-control-description">Lembra me</span>                            </label>                            <a class="text-muted hover-primary fs-13" href="#" data-toggle="modal" data-target="#modal-fill">Esqueceu senha?</a>                        </div>                        <div class="form-group">                            <button class="btn btn-bold btn-block btn-primary" type="submit">Acessar Sistema</button>                        </div>                    </form>                    <?php if ($config['cUsuario'] == '1') { ?>                        <hr class="w-30px">                        <p class="text-center text-muted fs-13 mt-20">Não tem uma conta? <a class="text-primary fw-500" href="#">Cadastrar</a></p>                    <?php } ?>                </div>            </div>            <div class="modal modal-fill fade" data-backdrop="false" id="modal-fill" tabindex="-1">                <div class="modal-dialog">                    <div class="modal-content">                        <div class="modal-header">                            <h5 class="modal-title">Redifinir Senha</h5>                            <button type="button" class="close" data-dismiss="modal">                                <span aria-hidden="true">&times;</span>                            </button>                            <button style="margin-top: 15px;" type="button" class="close" data-dismiss="modal">                                <span style="font-size: .85rem;">esc</span>                            </button>                        </div>                        <div class="modal-body form-type-material">                            <form class="form-type-material mt-0" id="autenticaSenha">                                <div class="form-group">                                    <input type="text" class="form-control" id="email" name="email">                                    <label for="username">E-mail</label>                                </div>                                <div class="form-group">                                    <button class="btn btn-bold btn-block btn-primary" id="btn_login">Recuperar Senha</button>                                </div>                            </form>                        </div>                        <div class="modal-footer">                            <button type="button" class="btn btn-bold btn-pure btn-primary">Logar</button>                        </div>                    </div>                </div>            </div>        </div>        <!-- Scripts -->        <script src="<?php echo base_url('assets/js/core.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/app.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/script.min.js'); ?>"></script>        <script src="<?php echo base_url('assets/js/jquery_validar.js') ?>"></script>        <script type="text/javascript">            $(document).ready(function () {                $('#email').focus();                $("#autenticaLogin").validate({                    rules: {                        email: {required: true, email: true},                        senha: {required: true, minlength: 6}                    },                    messages: {                        email: {required: 'Campo Requerido.', email: 'Insira Email válido'},                        senha: {required: 'Campo Requerido.', minlength: jQuery.validator.format("Pelo menos {0} caracteres são necessários!")}                    },                    submitHandler: function (form) {                        var dados = $(form).serialize();                        $.ajax({                            type: "POST",                            url: "<?php echo base_url('login/autenticarUser?ajax=true'); ?>",                            data: dados,                            dataType: 'json',                            success: function (data) {                                if (data.result == true) {                                    window.location.href = "<?php echo $this->input->get('next'); ?>";                                } else {                                    app.modaler({                                        html: 'Os dados de acesso estão incorretos, por favor tente novamente!',                                        type: 'bottom',                                        title: 'Error ao Acessar',                                        size: 'sm',                                    });                                }                            }                        });                        return false;                    },                    errorClass: "is-invalid",                    errorElement: "invalid-feedback",                    highlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').addClass('is-invalid');                    },                    unhighlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').removeClass('is-invalid');                        $(element).parents('.is-valid').addClass('is-valid');                    }                });                                $("#autenticaSenha").validate({                    rules: {                        email: {required: true, email: true}                    },                    messages: {                        email: {required: 'O campo email é obrigatorio.', email: 'O campo email deve conter um email válido.'}                    },                     submitHandler: function (form) {                        var dados = $(form).serialize();                        $.ajax({                            type: "POST",                            url: "<?php echo base_url('login/recuperar'); ?>",                            data: dados,                            dataType: 'json',                            beforeSend: function() {	                                $("#btn_login").html("Aguarde..... Validando dados!");                            },                            success: function (data){                                if (data.result == true) {                                    $("#autenticaSenha")[0].reset();                                    app.toast(data.mensagen);                                    $("#btn_login").html('Recuperar Senha');                                } else {                                    $("#btn_login").html('Recuperar Senha');                                    $(".msg").css('display', 'block')                                    app.toast(data.mensagen);                                }                            }                        });                        return false;                    },                    errorClass: "help-inline",                    errorElement: "span",                    highlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').addClass('is-invalid');                    },                    unhighlight: function (element, errorClass, validClass) {                        $(element).parents('.is-invalid').removeClass('is-invalid');                        $(element).parents('.is-valid').addClass('is-valid');                    }                });                                $(document).on('click', '.close', function (event) {                    $("#autenticaSenha")[0].reset();                });            });        </script>    </body></html>