	    cnpj_cpf: {required: true},	    tipo_ie_cliente: {required: true} ,	    cep_cliente: {required: true},	    endereco_cliente: {required: true},            numero_cliente: {required: true},            bairro_cliente: {required: true},            uf_cliente: {required: true}	},	messages: {            razao_social_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    cnpj_cpf: {required: 'Este campo e de preenchimento obrigatório'},	    tipo_ie_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    cep_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    endereco_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    numero_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    bairro_cliente: {required: 'Este campo e de preenchimento obrigatório'},	    uf_cliente: {required: 'Este campo e de preenchimento obrigatório'}	},	errorClass: "help-inline",	errorElement: "span",	highlight: function (element, errorClass, validClass) {	    $(element).parents('.mensg').addClass('danger');	},	unhighlight: function (element, errorClass, validClass) {	    $(element).parents('.mensg').removeClass('danger');	    $(element).parents('.mensg').addClass('success');	},        submitHandler: function( form ){	   var dados = $( form ).serialize();	    $.ajax({		type: "POST",		url: "<?php echo base_url('clientes/editarCliente'); ?>",		data: dados,		dataType: 'json',		success: function(ret) {		  if(ret.result == true){                        $('#sucesso').modal('show');                        $('.mensagem').html(ret.mensagen);		    }else{                         $('#error').modal('show');                    }		}	    });	    return false;	}    });});function proximoCampo(atual,proximo){    if(atual.value.length >= atual.maxLength){	document.getElementById(proximo).focus();    }}</script>