<div class="main-content">    <div class="row">        <div class="col-12">            <div class="card">                <?php if($this->permission->checkPermission($this->session->userdata('permissao'),'aPermissao')){ ?>                    <h4 class="card-title">                        <button onclick="location.href = '<?php echo base_url('permissoes/adicionar'); ?>'" class="btn btn-primary text-right"><i class="icon ti-plus"></i> Adicionar Permissão</button>                    </h4>                  <?php } ?>                <div class="card-body">                    <p>Lista de permissões cadastrada no sistema.</p>                    <br>                    <?php                        if ($this->session->flashdata('error') != null || $this->session->flashdata('success') != null) {                            if($this->session->flashdata('success') == TRUE){                                $alert = 'primary';                            } else {                                $alert = 'danger';                            }                    ?>                    <div class="alert alert-<?php echo $alert; ?>" role="alert">                            <?php                                if ($this->session->flashdata('success') == TRUE) {                                    echo '<i class="zmdi zmdi-check"></i> '. $this->session->flashdata('success');                                 } else {                                    echo 'Ops, um error encontrado! <br> <b>Drescrição do Erro:</b> <br>'. $this->session->flashdata('error');                                 }                            ?>                    </div>                    <?php } ?>                    <table class="table table-striped" cellspacing="0" data-provide="datatables">                        <thead>                            <tr>                                <th>#</th>                                <th>Nome</th>                                <th>Data do cadastro</th>                                <th>Situação</th>                                <th></th>                            </tr>                        </thead>                        <tbody>                                <?php if(!$obterPermissao){?>                                    <tr class="text-center"><td colspan="6">Nenhuma usuário encontrado.</td></tr>                                <?php                                 }else{                                     foreach ($obterPermissao as $r) {                                        if($r->situacao == '1'){ $lab = 'success'; $tipo = 'Ativo';}elseif($r->situacao == '2'){$lab = 'danger'; $tipo = 'Inativo';}                                ?>                                <tr>                                    <td><?php echo $r->idPermissao; ?></td>                                    <td><?php echo $r->nPermissao; ?></td>                                    <td><?php echo date('d/m/Y', strtotime($r->criado)) ?></td>                                    <td class="text-<?php echo $lab; ?>"><b><?php echo $tipo; ?></b></td>                                    <td>                                        <?php                                         if($this->permission->checkPermission($this->session->userdata('permissao'),'dPermissao') && $this->permission->checkPermission($this->session->userdata('permissao'),'ePermissao')){                                            if($r->situacao == 1){                                                echo '<a href="#bloquear-0"  role="button" data-toggle="modal" bloquear="'.$r->idPermissao.'" id="situacao2" style="margin-right: 1%" class="btn btn-warning btn-sm" title="Bloquear Usuário"><i class="icon ti-lock"></i></a>';                                             }else{                                                echo '<a href="#ativar-1"  role="button" data-toggle="modal" ativar="'.$r->idPermissao.'" id="situacao1" style="margin-right: 1%" class="btn btn-success btn-sm" title="Ativar Usuário"><i class="icon ti-unlock"></i></a>';                                             }                                        }                                        ?>                                        <?php if($this->permission->checkPermission($this->session->userdata('permissao'),'ePermissao')){ ?>                                            <a href="<?php echo base_url('permissoes/editar/'.$r->idPermissao); ?>" href="" class="btn btn-info btn-sm"><i class="icon ti-pencil-alt"></i></a>                                        <?php } ?>                                    </td>                                </tr>                                     <?php } ?>                                <?php } ?>                            </tr>                        </tbody>                    </table>                </div>            </div>        </div>    </div></div><div class="modal fade" id="ativar-1" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">    <div class="modal-dialog modal-sm" role="document">        <form action="<?php echo base_url() ?>permissoes/ativar" method="post" >            <div class="modal-content">                <div class="modal-header">                    <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                    <h4 class="modal-title" id="myModalLabel">Notificação</h4>                </div>                <div class="modal-body">                    <input type="hidden" id="ativaPermissao" name="id" value="" />                    <div class="meg"><p style="text-align: center">Deseja realmente Ativa este permissão?</p></div>                </div>                <div class="modal-footer">                    <button type="button" class="btn btn-danger" data-dismiss="modal">Não</button>                    <button class="btn btn-success">Sim</button>                </div>            </div>        </form>    </div></div><div class="modal fade" id="bloquear-0" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">    <div class="modal-dialog modal-sm" role="document">        <form action="<?php echo base_url() ?>permissoes/bloquear" method="post" >            <div class="modal-content">                <div class="modal-header">                    <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                    <h4 class="modal-title" id="myModalLabel">Notificação</h4>                </div>                <div class="modal-body">                    <input type="hidden" id="bloquearPermissao" name="id" value="" />                    <div class="meg"><p style="text-align: center">Deseja realmente bloquear este permissão?</p></div>                </div>                <div class="modal-footer">                    <button type="button" class="btn btn-danger" data-dismiss="modal">Não</button>                    <button class="btn btn-success">Sim</button>                </div>            </div>        </form>    </div></div>