 modal-footer-uniform">                <a class="editar" ></a>                <a class="excluir" ></a>                <button type="button" class="btn btn-bold btn-pure btn-secondary" data-dismiss="modal">Close</button>            </div>        </div>    </div></div><script type="text/javascript">$(document).ready(function () {$(document).on('click', '#excluirProduto', function (event) {    var cliente = $(this).attr('produtos');    $('#idProduto').val(cliente);});}); </script>